	def AddCustomersModule(customercode, name, address, phone, email, payterm, discount, currency, taxing, paymethod, carrier, remarks):

		conn = Database.ConnectDatabase()

		cursor = conn.cursor()

		cursor.execute("SELECT * FROM Customers WHERE CusCode = %s",(customercode,))

		results = cursor.fetchall()

		code = customercode

		namelower = name.lower()

		if results:

			cursor.execute("SELECT * FROM Customers WHERE LOWER(CusName) LIKE %s",(namelower,))

			nameexist = cursor.fetchall()

			if nameexist:

				for n in nameexist:

					code = n[0]

			if code != customercode:

				return "Customer name already exists."

			else:

				cursor.execute("UPDATE Customers SET CusName = %s, CusAddress = %s, CusPhone = %s, CusEmail = %s, CusPayTerm = %s, CusDiscount = %s, CusCurrency = %s, CusTaxing = %s, CusPayMethod = %s, CusCarrier = %s, CusRemarks = %s WHERE CusCode = %s", (name, address, phone, email, payterm, discount, currency, taxing, paymethod, carrier, remarks, customercode))

				conn.commit()

				return "Customer successfully updated."

		else:

			cursor.execute("SELECT * FROM Customers WHERE LOWER(CusName) LIKE %s",(namelower,))

			results = cursor.fetchall()

			if results:

				return "Customer name already exists."

