	#Print report to PDF.
	def PrintReportToPDF(self):

		datum = ""

		try:
			
			f = open("Temp.html", "r", encoding="utf-8")

			for line in f.readlines():

				if "<body>" in line:

					line += "<h5 style=\"text-align: center;font-family: Raleway;\">" + self.ReportNameLabel.text() + "</h5>\n"

				datum += line

			f.close()

			self.loader = QWebView()
			self.loader.setHtml(datum)

			self.DirectoryName = QFileDialog.getExistingDirectory(self, 'Select Directory')
			self.ReportDirectory = ""

			if self.DirectoryName:

				self.ReportDirectory = self.DirectoryName + "/" + self.ReportNameLabel.text() + ".pdf"
				self.loader.page().printToPdf(self.ReportDirectory)

				self.MessageBox("File saved successfully.", "OkType", "InfoStyle")

		except Exception as e:

			self.MessageBox("Error converting to PDF file.", "OkType", "AlertStyle")

