	def UserComboboxSelectItem(self, i):

		ComboName = self.sender()

		global ControlRegister
		global Module
		global PreviousModule
		global ScreenSize

		if ComboName.objectName() == "UserComboBox":

			if i == 0:

				self.MyTimer.stop()
				self.FooterFrame.hide()
				self.HomeFrame.raise_()

			elif i == 1:

				Module = "LogIn"
