	#Change balance in Purcahse module.
	def PurchaseBalance(self):
    		
		if self.AddPurchasePaidLineEdit.text() != "":
    		
			self.AddPurchaseBalanceLineEdit.setText(str(float(self.AddPurchaseTotalLineEdit.text()) - float(self.AddPurchasePaidLineEdit.text())))

			if float(self.AddPurchaseBalanceLineEdit.text()) < 0:

				self.AddPurchaseBalanceLineEdit.setText("0.0")
		
		else:
    		
			self.AddPurchaseBalanceLineEdit.setText(self.AddPurchaseTotalLineEdit.text())

	#Keypress digit.
	def DigitPressed(self, requester):

		try:
		
			button = self.sender()

			if button == None:

				button = requester

			else:

				button = button.text()

			if (('.' in self.AddSaleCalcPaidLineEdit.text()) and (button == "0")):

				newlabel = format(self.AddSaleCalcPaidLineEdit.text() + button,'.15')

			else:

				newlabel = format(float(self.AddSaleCalcPaidLineEdit.text() + button),'.15g')

			self.AddSaleCalcPaidLineEdit.setText(newlabel)
			self.AddSalePaidLineEdit.setText(self.AddSaleCalcPaidLineEdit.text())

			self.AddSaleBalanceLineEdit.setText(str(float(self.AddSaleTotalLineEdit.text()) - float(self.AddSalePaidLineEdit.text())))
			if float(self.AddSaleBalanceLineEdit.text()) < 0:self.AddSaleBalanceLineEdit.setText("0.0")

			self.AddSaleChangeLineEdit.setText(str(float(self.AddSalePaidLineEdit.text()) - float(self.AddSaleTotalLineEdit.text())))
			if float(self.AddSaleChangeLineEdit.text()) < 0:self.AddSaleChangeLineEdit.setText("0.0")

		except Exception as e:	

			global Module
			Module = "Nothing"

			self.MessageBox("Maximum amount reach.", "OkType", "AlertStyle")
			self.AddSaleCalcPaidLineEdit.setText("0")

	#Decimal pressed.
	def DecimalPressed(self):

		if "." not in self.AddSaleCalcPaidLineEdit.text():

			self.AddSaleCalcPaidLineEdit.setText(self.AddSaleCalcPaidLineEdit.text() + '.')
			self.AddSalePaidLineEdit.setText(self.AddSaleCalcPaidLineEdit.text())

			self.AddSaleBalanceLineEdit.setText(str(float(self.AddSaleTotalLineEdit.text()) - float(self.AddSalePaidLineEdit.text())))
			if float(self.AddSaleBalanceLineEdit.text()) < 0:self.AddSaleBalanceLineEdit.setText("0.0")

			self.AddSaleChangeLineEdit.setText(str(float(self.AddSalePaidLineEdit.text()) - float(self.AddSaleTotalLineEdit.text())))
			if float(self.AddSaleChangeLineEdit.text()) < 0:self.AddSaleChangeLineEdit.setText("0.0")

