	def keyPressEvent(self, e):

		global Module

		if Module == "VendorLogoPushButton":

			if self.VendorTabWidget.currentIndex() == 0:

				if e.key() == Qt.Key_Return or e.key() == Qt.Key_Enter:self.SaveButton("AddVendorSavePushButton")

