	#Event listener.
	def eventFilter(self, obj, event):

		if event.type() == QtCore.QEvent.KeyPress and obj is self.SelectItemsComboBox:

			if event.key() == QtCore.Qt.Key_Return and self.SelectItemsComboBox.hasFocus():

				self.ComboBoxEvents("SelectItemsComboBox", "None")

		return super().eventFilter(obj, event)

	#Initialize database.
	def InitializeDatabase(self):

		global Module
		global PreviousModule

		params = config()

		if params == "None":

			self.SetupDatabaseFrame.show()

		else:

			checkdb = Database.CheckDatabase()

			if checkdb == "existed":

				self.SetupDatabaseFrame.hide()

				Module = "LogIn"
				PreviousModule = Module

				self.AutoFillLogin()
				self.DisableRegisteringNewAccount()

			else:

				self.SetupDatabaseFrame.show()

	#Setup database.
	def SetupDatabase(self):

		global Module
		global PreviousModule

		if self.SetupDatabaseHostLineEdit.text() != "" and self.SetupDatabaseNameLineEdit.text() != "" and self.SetupDatabaseUserLineEdit.text() != "" and self.SetupDatabasePasswordLineEdit.text() != "":

			f = open("database.ini", "w", encoding="utf-8")
			f.write("[postgresql]\n" + "host=" + self.SetupDatabaseHostLineEdit.text() + "\n" + "database=postgres\n" + "user=" + self.SetupDatabaseUserLineEdit.text() + "\n" + "password=" + self.SetupDatabasePasswordLineEdit.text())
			f.close()

			checkdb = Database.CheckDatabase()

			if checkdb == "Failed to connect to database.":

				self.MessageBox("User password authentication failed.", "OkType", "AlertStyle")
				self.SetupDatabaseFrame.show()

			else:

				if checkdb == "not existed":

					Database.CreateDatabase()
					Database.CreateDefaultUser("administrator", "DanialSoftPOS@123", "danialsoftpos@gmail.com", "Administrator")

				self.MessageBox("Database successfully created.", "OkType", "InfoStyle")
				self.SetupDatabaseFrame.hide()

				Module = "LogIn"
				PreviousModule = Module

		else:

			self.MessageBox("Please fill all blank fields.", "OkType", "AlertStyle")

